// JavaScript code (index.js)

// Recipe data structure
let recipes = [];
let currentPage = 1;
const recipesPerPage = 3;

function addRecipe() {
    // Get input values
    let title = document.getElementById('title').value;
    let ingredients = document.getElementById('ingredients').value;
    let instructions = document.getElementById('instructions').value;
    let image = document.getElementById('image').value;
  
    // Validate required fields
    if (title.trim() === '' || ingredients.trim() === '' || instructions.trim() === '') {
      alert('Please fill in all the required fields.');
      return;
    }
  
    // Create a new recipe object
    let recipe = {
      title: title,
      ingredients: ingredients,
      instructions: instructions,
      image: image
    };
  
    // Add the recipe to the data structure
    recipes.push(recipe);
  
    // Clear the form fields
    document.getElementById('title').value = '';
    document.getElementById('ingredients').value = '';
    document.getElementById('instructions').value = '';
    document.getElementById('image').value = '';
  
    // Save the recipes to local storage
    saveRecipes();
  
    // Update the current page based on the new recipe count
    currentPage = Math.ceil(recipes.length / recipesPerPage);
  
    // Refresh the recipe list
    displayRecipes(currentPage);
  }
  
// Function to search for recipes based on title or ingredients
function searchRecipes() {
  let searchTerm = document.getElementById('searchInput').value.toLowerCase();
  let filteredRecipes = recipes.filter(function(recipe) {
    return (
      recipe.title.toLowerCase().includes(searchTerm) ||
      recipe.ingredients.toLowerCase().includes(searchTerm)
    );
  });

  displayRecipes(currentPage, filteredRecipes);
}
// JavaScript code (index.js)
document.getElementById('recipeForm').addEventListener('submit', function(event) {
    event.preventDefault(); // Prevent form submission
  
    // Get form inputs
    var title = document.getElementById('title').value;
    var ingredients = document.getElementById('ingredients').value;
    var instructions = document.getElementById('instructions').value;
    var imageInput = document.getElementById('image');
  
    // Create a new FileReader
    var reader = new FileReader();
  
    // Define a function to handle the file load event
    reader.onload = function(event) {
      var imageUrl = event.target.result; // Get the image data URL
  
      // Create a recipe object with the form inputs and image URL
      var recipe = {
        title: title,
        ingredients: ingredients,
        instructions: instructions,
        image: imageUrl
      };
  
      // Process the recipe data (e.g., display it, store it, etc.)
      processRecipe(recipe);
    };
  
    // Read the uploaded image file as a data URL
    reader.readAsDataURL(imageInput.files[0]);
  });
  
  function processRecipe(recipe) {
    // Display the recipe, store it, or perform any desired actions
    console.log(recipe);
  }
  

// Function to display the list of recipes
function displayRecipes(page, recipeData = recipes) {
    let startIndex = (page - 1) * recipesPerPage;
    let endIndex = startIndex + recipesPerPage;
    let recipeList = document.getElementById('recipeList');
    recipeList.innerHTML = ''; // Clear previous content
  
    // Loop through each recipe and create/update list items
    for (let i = startIndex; i < endIndex && i < recipeData.length; i++) {
      let recipe = recipeData[i];
  
      let col = document.createElement('div');
      col.className = 'col-lg-4 col-md-6 mb-4';
      col.innerHTML = `
        <div class="card recipe-card">
          <img src="${recipe.image}" class="card-img-top" alt="Recipe Image" style="height: 200px; object-fit: cover;">
          <div class="card-body">
            <h5 class="card-title">${recipe.title}</h5>
            <p class="card-text"><strong>Ingredients:</strong> ${recipe.ingredients}</p>
            <p class="card-text"><strong>Instructions:</strong> ${recipe.instructions}</p>
            <button onclick="editRecipe(${i})" class="btn btn-primary">Edit</button>
            <button onclick="deleteRecipe(${i})" class="btn btn-danger">Delete</button>
          </div>
        </div>
      `;
  
      recipeList.appendChild(col);
    }
  
    // Update the pagination buttons
    updatePaginationButtons(page, recipeData.length);
  }
  

// Function to update the pagination buttons
function updatePaginationButtons(currentPage, totalRecipes) {
  let totalPages = Math.ceil(totalRecipes / recipesPerPage);
  let pagination = document.getElementById('pagination');
  pagination.innerHTML = ''; // Clear previous buttons

  // Create the Previous button
  let previousButton = document.createElement('button');
  previousButton.className = 'btn btn-primary';
  previousButton.innerText = 'Previous';
  previousButton.disabled = currentPage === 1;
  previousButton.addEventListener('click', function() {
    if (currentPage > 1) {
      currentPage--;
      displayRecipes(currentPage);
    }
  });
  pagination.appendChild(previousButton);

  // Create the Next button
  let nextButton = document.createElement('button');
  nextButton.className = 'btn btn-primary';
  nextButton.innerText = 'Next';
  nextButton.disabled = currentPage === totalPages;
  nextButton.addEventListener('click', function() {
    if (currentPage < totalPages) {
      currentPage++;
      displayRecipes(currentPage);
    }
  });
  pagination.appendChild(nextButton);
}

