// Function to save recipes to local storage
function saveRecipes() {
  localStorage.setItem('recipes', JSON.stringify(recipes));
}

// Function to edit a recipe
function editRecipe(index) {
  // Retrieve the recipe from the data structure
  let recipe = recipes[index];

  // Set the form values to the recipe data
  document.getElementById('title').value = recipe.title;
  document.getElementById('ingredients').value = recipe.ingredients;
  document.getElementById('instructions').value = recipe.instructions;
  document.getElementById('image').value = recipe.image;

  // Remove the recipe from the data structure
  recipes.splice(index, 1);

  // Save the recipes to local storage
  saveRecipes();

  // Refresh the recipe list
  displayRecipes(currentPage);
}

// Function to delete a recipe
function deleteRecipe(index) {
  // Confirm the deletion with the user
  if (confirm('Are you sure you want to delete this recipe?')) {
    // Remove the recipe from the data structure
    recipes.splice(index, 1);

    // Save the recipes to local storage
    saveRecipes();

    // Refresh the recipe list
    displayRecipes(currentPage);
  }
}

// Initialize the page
initializePage();

// Add event listener to the "Add Recipe" button
document.getElementById('addRecipeBtn').addEventListener('click', addRecipe);